export type { AvatarOutfitHrid }
export type AvatarOutfitId = keyof typeof AVATAR_OUTFITS
export type AvatarOutfitData = typeof AVATAR_OUTFITS

export const FREE_AVATAR_OUTFITS = Object.values(AVATAR_OUTFITS).filter(outfit => outfit.cowbellCost === 0)

export const PAID_AVATAR_OUTFITS = Object.values(AVATAR_OUTFITS).filter(outfit => outfit.cowbellCost > 0)

export const SEASONAL_AVATAR_OUTFITS = Object.values(AVATAR_OUTFITS).filter(outfit => outfit.isSeasonal)

export const NON_SEASONAL_AVATAR_OUTFITS = Object.values(AVATAR_OUTFITS).filter(outfit => !outfit.isSeasonal)

export const AVATAR_OUTFITS_SORTED = [...Object.values(AVATAR_OUTFITS)].sort((a, b) => a.sortIndex - b.sortIndex)


export function getAvatarOutfitCost(hrid: AvatarOutfitHrid): number {
  return AVATAR_OUTFITS[hrid].cowbellCost
}

export function isAvatarOutfitFree(hrid: AvatarOutfitHrid): boolean {
  return AVATAR_OUTFITS[hrid].cowbellCost === 0
}

export function isAvatarOutfitSeasonal(hrid: AvatarOutfitHrid): boolean {
  return AVATAR_OUTFITS[hrid].isSeasonal
}

export function getAvatarOutfitsByCostRange(minCost: number, maxCost: number): AvatarOutfit[] {
  return Object.values(AVATAR_OUTFITS).filter(
    outfit => outfit.cowbellCost >= minCost && outfit.cowbellCost <= maxCost
  )
}

export function getAffordableAvatarOutfits(cowbells: number): AvatarOutfit[] {
  return Object.values(AVATAR_OUTFITS).filter(outfit => outfit.cowbellCost <= cowbells)
}